<?php\n// Database configuration\n$host = 'localhost';\n$username = 'your_username';\n$password = 'your_password';\n$database = 'your_database';\n\n// Create backup file name\n$backupFile = $database . '_' . date('Y-m-d_H-i-s') . '.sql';\n\n// Command to create database backup\n$command = "mysqldump --user={$username} --password={$password} --host={$host} {$database} > {$backupFile}";\n\n// Execute the command\nsystem($command);\n\n// Set headers to download the file\nheader('Content-Description: File Transfer');\nheader('Content-Type: application/octet-stream');\nheader('Content-Disposition: attachment; filename=' . basename($backupFile));\nheader('Expires: 0');\nheader('Cache-Control: must-revalidate');\nheader('Pragma: public');\nheader('Content-Length: ' . filesize($backupFile));\n\n// Read the file and download\nreadfile($backupFile);\n\n// Delete backup file from server after download\nunlink($backupFile);\n?>