$options = array(\n    CURLOPT_RETURNTRANSFER => true,     // return web page\n    CURLOPT_HEADER         => false,    // don't return headers\n    CURLOPT_FOLLOWLOCATION => true,     // follow redirects\n    CURLOPT_ENCODING       => "",       // handle all encodings\n    CURLOPT_USERAGENT      => "Mozilla/5.0 (Windows; U; Windows NT 5.1; en-US; rv:x.x.x) Gecko/20041107 Firefox/x.x", // who am i\n    CURLOPT_AUTOREFERER    => true,     // set referer on redirect\n    CURLOPT_CONNECTTIMEOUT => 120,      // timeout on connect\n    CURLOPT_TIMEOUT        => 120,      // timeout on response\n    CURLOPT_MAXREDIRS      => 10,       // stop after 10 redirects\n    CURLOPT_SSL_VERIFYHOST => 2,        // verify the host\n    CURLOPT_SSL_VERIFYPEER => true,     // verify the peer's SSL certificate\n    CURLOPT_CAINFO         => "/path/to/cacert.pem", // path to the CA bundle\n);\n\n// Initialize cURL session\n$ch = curl_init("https://secure-website.com");\n\n// Set cURL options\ncurl_setopt_array($ch, $options);\n\n// Execute cURL session\n$response = curl_exec($ch);\n\n// Check for cURL errors\nif (curl_errno($ch)) {\n    echo 'cURL error: ' . curl_error($ch);\n} else {\n    echo 'Response: ' . $response;\n}\n\n// Close cURL session\ncurl_close($ch);