<?php\n// Assuming $pdo is your PDO connection\n$updateStmt = $pdo->prepare("UPDATE A SET type = :type, sub_type = :sub_type, filename = :filename, filepath = :filepath, filesize = :filesize WHERE id = :id");\n\ntry {\n    $pdo->beginTransaction();\n    $updateStmt->execute([\n        ':type' => $type,\n        ':sub_type' => $sub_type,\n        ':filename' => $filename,\n        ':filepath' => $filepath,\n        ':filesize' => $filesize,\n        ':id' => $id\n    ]);\n    $pdo->commit();\n} catch (Exception $e) {\n    $pdo->rollBack();\n    echo "Failed to update record: " . $e->getMessage();\n}\n?>