$mysqli = new mysqli("host", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// First, execute the SELECT query\n$selectQuery = "SELECT itemid, points FROM items WHERE id='1'";\n$selectResult = $mysqli->query($selectQuery);\n\nif (!$selectResult) {\n    die('Invalid query: ' . $mysqli->error);\n}\n\n// Then, execute the UPDATE query\n$updateQuery = "UPDATE accounts SET userpoints = '1000' WHERE some_condition"; // Make sure to specify the right condition\n$updateResult = $mysqli->query($updateQuery);\n\nif (!$updateResult) {\n    die('Invalid query: ' . $mysqli->error);\n}\n\n$mysqli->close();