<?php\n// Assume $pdo is your PDO connection object\n$TempSQL = "SELECT field1, field2, field3 FROM table WHERE ";\n$params = [];\n\nif ($numberParams == 1) {\n    $TempSQL .= "field1 = :val1";\n    $params[':val1'] = $val1;\n} else {\n    $TempSQL .= "field2 = :val2 AND field3 = :val3";\n    $params[':val2'] = $val2;\n    $params[':val3'] = $val3;\n}\n\n$stmt = $pdo->prepare($TempSQL);\n$stmt->execute($params);\n\n// Fetch the results\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n?>