<?php\n$language = substr($_SERVER['HTTP_ACCEPT_LANGUAGE'], 0, 2);\n\nif ($language == 'en') {\n    // Check for specific English variants\n    $locale = substr($_SERVER['HTTP_ACCEPT_LANGUAGE'], 3, 2);\n    if ($locale == 'US' || $locale == 'us') {\n        include('http://page.com/us.txt');\n    } else {\n        include('http://page.com/uk.txt');\n    }\n} else {\n    // Default to UK if not US or language not recognized\n    include('http://page.com/uk.txt');\n}\n?>