<?php\n$xmlString = '<?xml version="1.0" encoding="UTF-8"?>\n<targets>\n  <showcases>\n    <building name="Big Blue" />\n    <building name="Shiny Red" />\n    <building name="Mellow Yellow" />\n  </showcases>\n</targets>';\n\n$doc = new DOMDocument();\n$doc->loadXML($xmlString);\n\n$xpath = new DOMXPath($doc);\n$buildingName = 'Shiny Red'; // change this to the name you want to check\n$query = sprintf('//building[@name="%s"]', $buildingName);\n\n$entries = $xpath->query($query);\n\nif ($entries->length > 0) {\n    echo "Building with name '$buildingName' exists.";\n} else {\n    echo "Building with name '$buildingName' does not exist.";\n}\n?>