<?php\nclass DbConnect {\n    private $dbhost = 'xx';\n    private $dbuser = 'xx';\n    private $dbpass = 'xx';\n    private $dbdatabase = 'xx';\n    private $connection;\n\n    public function __construct() {\n        $this->createDbConnection();\n    }\n\n    private function createDbConnection() {\n        try {\n            $this->connection = new PDO("mysql:host=$this->dbhost;dbname=$this->dbdatabase", $this->dbuser, $this->dbpass);\n            $this->connection->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n        } catch (PDOException $e) {\n            die("Could not connect: " . $e->getMessage());\n        }\n    }\n\n    public function getConnection() {\n        return $this->connection;\n    }\n\n    public function closeDbConnection() {\n        $this->connection = null;\n    }\n}\n\nclass DbInteractions {\n    private $connection;\n\n    public function __construct($connection) {\n        $this->connection = $connection;\n    }\n\n    public function searchDb($qstring) {\n        $qstring = htmlspecialchars($qstring, ENT_QUOTES, 'UTF-8');\n        $query = "SELECT content FROM site_content WHERE content LIKE :qstring";\n        $stmt = $this->connection->prepare($query);\n        $stmt->execute(['qstring' => "%$qstring%"]);\n\n        while ($result_row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n            echo "Result:";\n            foreach ($result_row as $out) {\n                echo $out . "<br>";\n            }\n        }\n    }\n\n    public function getPageContent($page) {\n        $query = "SELECT * FROM site_content WHERE page_id = :page";\n        $stmt = $this->connection->prepare($query);\n        $stmt->execute(['page' => $page]);\n\n        while ($result_row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n            echo "<div>" . htmlspecialchars($result_row['page_id']) . "</div>";\n            echo "<div>" . htmlspecialchars($result_row['content']) . "</div>";\n            echo "<div>" . htmlspecialchars($result_row['timestamp']) . "</div>";\n        }\n    }\n}\n?>