<?php\n// Function to set a default value if the variable is not set\nfunction setdefault(&$var, $default = "") {\n    if (!isset($var)) {\n        $var = $default;\n    }\n}\n\n// Check if 'id' is set in the query string and set $id accordingly\n$id = isset($_GET['id']) ? (int) $_GET['id'] : 0;\n\n// Alternatively, you can use the setdefault function for fallback\n// setdefault($id, 0);\n\necho $id;\n?>