function add_new_artwork_product() {\n    $product = new WC_Product_Simple();\n    $product->set_name('Artwork Title');\n    $product->set_price(100); // Set the price\n    $product->set_regular_price(100); // Set the regular price\n    $product->set_description('Detailed description of the artwork.');\n    $product->set_short_description('Short description of the artwork.');\n\n    // Set the product image (requires the ID of an uploaded image)\n    $image_id = attachment_url_to_postid( 'https://example.com/path/to/image.jpg' );\n    $product->set_image_id( $image_id );\n\n    $product_id = $product->save();\n    return $product_id;\n}