<?php\n    require 'vendor/autoload.php';\n\n    use MathPHP\LinearAlgebra\MatrixFactory;\n\n    // Create a matrix\n    $matrix = MatrixFactory::create([\n        [1, 2, 3],\n        [4, 5, 6],\n        [7, 8, 9],\n    ]);\n\n    // Transpose\n    $transpose = $matrix->transpose();\n\n    // Determinant\n    $determinant = $matrix->det();\n\n    // Inverse\n    $inverse = $matrix->inverse();\n\n    // Print results\n    echo "Transpose:\n";\n    print_r($transpose->getMatrix());\n\n    echo "Determinant: $determinant\n";\n\n    echo "Inverse:\n";\n    print_r($inverse->getMatrix());\n    ?>