// an numerical array of associative arrays\n$records = $dbResult->convertToArray();\n\nfunction customSort($a, $b) {\n    // Sort by 'Date Completed' DESC\n    if ($a['Date Completed'] != $b['Date Completed']) {\n        return strcmp($b['Date Completed'], $a['Date Completed']);\n    }\n    // Sort by 'Last Name' ASC\n    if ($a['Last Name'] != $b['Last Name']) {\n        return strcmp($a['Last Name'], $b['Last Name']);\n    }\n    // Sort by 'First Name' ASC\n    if ($a['First Name'] != $b['First Name']) {\n        return strcmp($a['First Name'], $b['First Name']);\n    }\n    // Sort by 'Course' ASC\n    return strcmp($a['Course'], $b['Course']);\n}\n\n// Sort the records array\nusort($records, 'customSort');