private $_messages;\n\npublic function init() {\n    $this->_messages = $this->_helper->FlashMessenger;\n}\n\npublic function loginAction() {\n    // > login validation <\n\n    // Switch based on the result code\n    switch ($result->getCode()) {\n        // > snip several cases <\n\n        case Zend_Auth_Result::FAILURE_CREDENTIAL_INVALID:\n            $this->_messages->addMessage("That wasn't the right password.");\n            $this->view->messages = ["That wasn't the right password."]; // Directly set message\n            break;\n\n        case Zend_Auth_Result::SUCCESS:\n            $this->_messages->addMessage('Logged you in successfully. Welcome back!');\n            $this->view->messages = ['Logged you in successfully. Welcome back!']; // Directly set message\n            $this->_helper->Redirector('index', 'home');\n            break;\n    }\n\n    // Render view with messages for immediate display\n    if (!isset($this->view->messages)) {\n        $this->view->messages = $this->_messages->getMessages();\n    }\n    $this->render();\n}