// Assuming you have two database connections set up: $db_master and $db_slave\nclass dbal_custom extends dbal\n{\n    protected $db_master;\n    protected $db_slave;\n\n    public function __construct($db_master, $db_slave)\n    {\n        $this->db_master = $db_master;\n        $this->db_slave = $db_slave;\n    }\n\n    public function sql_query($query = '', $cache_ttl = 0)\n    {\n        $query_type = strtoupper(substr(trim($query), 0, 6));\n        if (in_array($query_type, ['SELECT', 'SHOW'])) {\n            return $this->db_slave->sql_query($query, $cache_ttl);\n        } else {\n            return $this->db_master->sql_query($query, $cache_ttl);\n        }\n    }\n\n    public function sql_query_limit($query, $total, $offset = 0, $cache_ttl = 0)\n    {\n        // Assuming limit queries are generally read operations\n        $query_type = strtoupper(substr(trim($query), 0, 6));\n        if ($query_type === 'SELECT') {\n            return $this->db_slave->sql_query_limit($query, $total, $offset, $cache_ttl);\n        } else {\n            return $this->db_master->sql_query_limit($query, $total, $offset, $cache_ttl);\n        }\n    }\n\n    // Override other methods as necessary...\n}\n\n// Usage example\n$db_master = new dbal_mysqli();\n$db_slave = new dbal_mysqli();\n$db_master->sql_connect($dbhost, $dbuser, $dbpasswd, $dbname);\n$db_slave->sql_connect($dbhost, $dbuser, $dbpasswd, $dbname);\n\n$db = new dbal_custom($db_master, $db_slave);