<?php\n// Example date retrieved from the database\n$dateFromDatabase = '2009-04-13';\n\n// Create a DateTime object\n$dateTime = new DateTime($dateFromDatabase);\n\n// Set the locale to Spanish\nsetlocale(LC_TIME, 'es_ES.UTF-8');\n\n// Format the date to 'DD Month, YYYY' with month names in Spanish\n$formattedDate = strftime('%d %B, %Y', $dateTime->getTimestamp());\n\necho $formattedDate;\n?>