<?php\n// Define allowed error codes and corresponding messages\n$errorList = array(\n    11 => 'Error message for code 11',\n    12 => 'Error message for code 12',\n    // Add other error codes and messages here\n);\n\n// Check if the 'n' parameter is set and is a valid integer\nif (isset($_GET['n']) && filter_var($_GET['n'], FILTER_VALIDATE_INT)) {\n    $n = (int)$_GET['n'];\n\n    // Check if the error code exists in the error list\n    if (array_key_exists($n, $errorList)) {\n        // Display the error message\n        echo htmlspecialchars("Error: " . $errorList[$n], ENT_QUOTES, 'UTF-8');\n    } else {\n        // Display a generic error message for invalid error codes\n        echo htmlspecialchars("Error: Invalid error code.", ENT_QUOTES, 'UTF-8');\n    }\n} else {\n    // Display a generic error message if 'n' is not set or invalid\n    echo htmlspecialchars("Error: No error code provided.", ENT_QUOTES, 'UTF-8');\n}\n?>