<?php\n$hostnames = ['google.com', 'facebook.com', 'amazon.com'];\n$success = true;\n\nforeach ($hostnames as $hostname) {\n    $result = dns_get_record($hostname, DNS_A);\n    if ($result === false || empty($result)) {\n        $success = false;\n        echo "Failed to lookup $hostname\n";\n    } else {\n        echo "Success for $hostname\n";\n    }\n}\n\nif ($success) {\n    echo "All DNS lookups were successful.\n";\n} else {\n    echo "Some DNS lookups failed.\n";\n}\n?>