<?php\nrequire 'vendor/autoload.php';\n\nuse PhpOffice\PhpSpreadsheet\Spreadsheet;\nuse PhpOffice\PhpSpreadsheet\Writer\Xlsx;\nuse PhpOffice\PhpSpreadsheet\Worksheet\Worksheet;\n\n// Initialize the spreadsheet\n$spreadsheet = new Spreadsheet();\n\n// Function to add a new sheet with HTML content\nfunction addHtmlSheet($spreadsheet, $htmlContent, $sheetTitle) {\n    $worksheet = new Worksheet($spreadsheet, $sheetTitle);\n    $spreadsheet->addSheet($worksheet);\n    $spreadsheet->setActiveSheetIndexByName($sheetTitle);\n    \n    \PhpOffice\PhpSpreadsheet\Shared\Html::toRichTextObject($htmlContent);\n    $worksheet->getCell('A1')->setValue(\PhpOffice\PhpSpreadsheet\Shared\Html::toRichTextObject($htmlContent));\n}\n\n// Example HTML reports\n$htmlReport1 = '<table><tr><td>Report 1</td></tr></table>';\n$htmlReport2 = '<table><tr><td>Report 2</td></tr></table>';\n\n// Add reports as separate sheets\naddHtmlSheet($spreadsheet, $htmlReport1, 'Report 1');\naddHtmlSheet($spreadsheet, $htmlReport2, 'Report 2');\n\n// Remove the first default sheet created\n$spreadsheet->removeSheetByIndex(0);\n\n// Save the spreadsheet\n$writer = new Xlsx($spreadsheet);\n$writer->save('multi-tab-report.xlsx');\n?>