<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\ntry {\n    $conn = new PDO("mysql:host=$servername;dbname=$dbname", $username, $password);\n    // Set the PDO error mode to exception\n    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Get the orphaned IDs\n    $stmt = $conn->prepare("SELECT child.id\n                            FROM items AS child\n                            LEFT JOIN items AS parent ON child.parent_id = parent.id\n                            WHERE child.parent_id IS NOT NULL AND parent.id IS NULL");\n    $stmt->execute();\n    $orphaned_ids = $stmt->fetchAll(PDO::FETCH_COLUMN, 0);\n\n    // Delete the orphaned items\n    foreach ($orphaned_ids as $id) {\n        $delete_stmt = $conn->prepare("DELETE FROM items WHERE id = :id");\n        $delete_stmt->bindParam(':id', $id);\n        $delete_stmt->execute();\n    }\n\n    echo "Orphaned items deleted successfully.";\n} catch(PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}\n\n$conn = null;\n?>