<?php\n// Define your table columns\n$timestampColumns = ['timestamp1', 'timestamp2', 'timestamp3', 'timestamp4', 'timestamp5', 'timestamp6', 'timestamp7', 'timestamp8', 'timestamp9', 'timestamp10', 'timestamp11', 'timestamp12'];\n$otherColumns = ['field1', 'field2', 'field3'];\n\n// Construct the SELECT part of the query\n$selectParts = [];\n\nforeach ($timestampColumns as $column) {\n    $selectParts[] = "DATE_FORMAT($column, '%c/%e/%y') AS $column";\n}\n\nforeach ($otherColumns as $column) {\n    $selectParts[] = $column;\n}\n\n$selectQuery = implode(', ', $selectParts);\n\n// Complete SQL query\n$sql = "SELECT $selectQuery FROM your_table_name";\n\n// Execute the query\n// Assuming you have a PDO connection $pdo\n$stmt = $pdo->prepare($sql);\n$stmt->execute();\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n// Output or process your results\nforeach ($results as $row) {\n    print_r($row);\n}\n?>