<?php\n// Set the content type\nheader('Content-Type: image/png');\n\n// Load the placeholder image\n$image = imagecreatefrompng('placeholder.png');\n\n// Define text and font properties\n$rating = 5; // Example rating\n$text = "Your rating is: " . $rating;\n$font = 5; // Built-in font size (1-5)\n\n// Set the text color (white in this case)\n$textColor = imagecolorallocate($image, 255, 255, 255);\n\n// Calculate text position to be centered\n$imageWidth = imagesx($image);\n$imageHeight = imagesy($image);\n$textWidth = imagefontwidth($font) * strlen($text);\n$textHeight = imagefontheight($font);\n$x = ($imageWidth - $textWidth) / 2;\n$y = ($imageHeight - $textHeight) / 2;\n\n// Add the text to the image\nimagestring($image, $font, $x, $y, $text, $textColor);\n\n// Output the image\nimagepng($image);\n\n// Free up memory\nimagedestroy($image);\n?>