<?php\n\n// Load HTML file\n$contents = file_get_contents('local_secure.html');\n\n// Initialize DOMDocument and load HTML\n$dom = new DOMDocument();\nlibxml_use_internal_errors(true); // To suppress HTML parsing warnings\n$dom->loadHTML($contents);\nlibxml_clear_errors();\n\n// Initialize XPath\n$xpath = new DOMXPath($dom);\n\n// Query for all input elements\n$inputs = $xpath->query('//input');\n\n// Iterate over each input element and get its name and value\nforeach ($inputs as $input) {\n    $name = $input->getAttribute('name');\n    $value = $input->getAttribute('value');\n    echo "Name: $name, Value: $value\n";\n}\n\n?>