<?php\n// Start the session\nsession_start();\n\n// Assuming $_SESSION['colorcode'] is set somewhere in your code\nif (isset($_SESSION['colorcode'])) {\n    // Set the cookie to expire in 30 days\n    setcookie(\n        'colorcode',\n        $_SESSION['colorcode'],\n        time() + 60 * 60 * 24 * 30, // Expires in 30 days\n        '/', // Path\n        '',  // Domain (default to current domain)\n        false, // Secure (false for HTTP, true for HTTPS)\n        true // HttpOnly (true for security)\n    );\n}\n\n// Other PHP code here\n?>