<?php\n// Ensure the table name is a valid string (e.g., only alphanumeric characters and underscores).\n$day = $_GET['day'];\nif (preg_match('/^[a-zA-Z0-9_]+$/', $day)) {\n    try {\n        $link = new PDO("mysql:dbname=$dbname;host=127.0.0.1", $username, $password);\n        $link->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n        // Safely include the table name in the query\n        $query = $link->prepare("SELECT locality_name FROM `$day` GROUP BY locality_name ORDER BY locality_name DESC");\n        $query->execute();\n        $result = $query->fetchAll(PDO::FETCH_ASSOC);\n\n        $link = null;\n\n        // Do things with the $result.\n    } catch (PDOException $e) {\n        echo 'Connection failed: ' . $e->getMessage();\n    }\n} else {\n    echo 'Invalid table name.';\n}\n?>