function array_extract_keys_and_values($srcArray, $keys) {\n    return array_intersect_key($srcArray, array_flip($keys));\n}\n\n$data = array(\n    'personName' => 'John',\n    'personAge' => 99,\n    'personId' => 1,\n    // many more data you don't need here ...\n);\n\n$wantedKeys = array('personName', 'personAge', 'personId');\n$wantedValues = array_extract_keys_and_values($data, $wantedKeys);\n\necho $wantedValues['personName'];  // Outputs: John\necho $wantedValues['personAge'];   // Outputs: 99\necho $wantedValues['personId'];    // Outputs: 1