$data = array(\n    'personName' => 'John',\n    'personAge' => 99,\n    'personId' => 1,\n    // many more data you don't need here ...\n);\n\n// Specify the keys you want to extract\n$wantedKeys = array('personName', 'personAge', 'personId');\n\n// Extract the subset\n$wantedValues = array_intersect_key($data, array_flip($wantedKeys));\n\n// If you want to use list to assign these values to variables\nlist($name, $age, $id) = array_values($wantedValues);\n\necho $name;  // Outputs: John\necho $age;   // Outputs: 99\necho $id;    // Outputs: 1