// Assuming you have a PDO connection $pdo\n\nfunction updateCartItem($cart_id, $item_id, $quantity) {\n    global $pdo;\n    \n    $sql = "INSERT INTO cart_items (cart_id, item_id, quantity)\n            VALUES (:cart_id, :item_id, :quantity)\n            ON DUPLICATE KEY UPDATE quantity = VALUES(quantity)";\n    \n    $stmt = $pdo->prepare($sql);\n    $stmt->execute([\n        ':cart_id' => $cart_id,\n        ':item_id' => $item_id,\n        ':quantity' => $quantity\n    ]);\n}\n\n// Example usage\nupdateCartItem(1, 2, 3); // Updates the cart with cart_id = 1, item_id = 2, and sets quantity to 3