<?php\n// Database connection\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Query to fetch data including BLOB\n$query = "SELECT id, name, image_blob FROM your_table";\n$result = $mysqli->query($query);\n\nif ($result->num_rows > 0) {\n    // Start XML document\n    $xml = new SimpleXMLElement('<root/>');\n\n    while ($row = $result->fetch_assoc()) {\n        $item = $xml->addChild('item');\n        $item->addChild('id', $row['id']);\n        $item->addChild('name', htmlspecialchars($row['name']));\n\n        // Base64 encode the BLOB data\n        $encodedBlob = base64_encode($row['image_blob']);\n        $item->addChild('image_blob', $encodedBlob);\n    }\n\n    // Output XML\n    Header('Content-type: text/xml');\n    print($xml->asXML());\n} else {\n    echo "0 results";\n}\n\n$mysqli->close();\n?>