$address = 'udp://x.x.x.x';\n$port = 12345;\n\n// Create a UDP socket server\n$server = stream_socket_server("$address:$port", $errno, $errstr, STREAM_SERVER_BIND);\nif (!$server) {\n    die("Error: $errstr ($errno)\n");\n}\n\necho "Listening on $address:$port...\n";\n\nwhile (true) {\n    $pkt = stream_socket_recvfrom($server, 200, 0, $peer);\n    if ($pkt !== false) {\n        echo "Received: $pkt from $peer\n";\n    }\n}