<?php\nrequire_once 'vendor/autoload.php';\n\nuse PHPRtfLite;\n\nPHPRtfLite::registerAutoloader();\n\nfunction splitRtfByPages($rtfFilePath) {\n    $rtf = new PHPRtfLite();\n    $rtf->loadRtfFile($rtfFilePath);\n\n    // Get the RTF content\n    $rtfContent = $rtf->getRtf();\n\n    // Define a heuristic or custom marker for page breaks\n    $pageBreakMarker = '\page';\n    $pages = explode($pageBreakMarker, $rtfContent);\n\n    return $pages;\n}\n\n$rtfFilePath = 'path/to/your/document.rtf';\n$pages = splitRtfByPages($rtfFilePath);\n\nforeach ($pages as $index => $pageContent) {\n    // Save or display each page content\n    file_put_contents("page_$index.rtf", $pageContent);\n    echo "Page $index content saved.\n";\n}\n?>