<?php\n// Get the current date in YYYY-MM-DD format\n$Date = date("Y-m-d");\n\n// Convert the date format to m/d/Y for comparison\n$DateFormatted = date("m/d/Y", strtotime($Date));\n\n// Query the database\n$result = mysql_query("SELECT * FROM service WHERE SType = 'Retreat' AND STR_TO_DATE(Start_date, '%m/%d/%Y') > '$Date'");\n\n// Check and fetch the results\nwhile ($row = mysql_fetch_assoc($result)) {\n    // Process the row\n    print_r($row);\n}\n?>