<?php\nif (isset($_GET['lat1']) && isset($_GET['lng1']) && isset($_GET['lat2']) && isset($_GET['lng2'])) {\n    $lat1 = $_GET['lat1'];\n    $lng1 = $_GET['lng1'];\n    $lat2 = $_GET['lat2'];\n    $lng2 = $_GET['lng2'];\n\n    $apiKey = 'YOUR_GOOGLE_MAPS_API_KEY';\n    $url = "https://maps.googleapis.com/maps/api/distancematrix/json?units=metric&origins=$lat1,$lng1&destinations=$lat2,$lng2&key=$apiKey";\n\n    $response = file_get_contents($url);\n    $data = json_decode($response, true);\n\n    if ($data['status'] == 'OK') {\n        $distance = $data['rows'][0]['elements'][0]['distance']['text'];\n        echo json_encode(['distance' => $distance]);\n    } else {\n        echo json_encode(['error' => 'Unable to get distance']);\n    }\n} else {\n    echo json_encode(['error' => 'Invalid parameters']);\n}\n?>