class Admin extends CI_Controller {\n    public function updateticket($ticketId) {\n        $this->load->model('m_tickets');\n        \n        // Get the action log from POST data\n        $actionLog = $this->input->post('actionlog');\n        \n        // Update the action log in the database\n        $this->m_tickets->appendActionLog($ticketId, $actionLog);\n        \n        // Retrieve the updated action log\n        $data['actionlog'] = $this->m_tickets->actionLogPull($ticketId);\n        \n        // Load the view and return the updated log\n        $this->load->view('content/ajaxtestform', $data);\n    }\n}