class M_tickets extends CI_Model {\n    public function actionLogPull($requestedNum = NULL) {\n        $this->db->where('ticketnum', $requestedNum);\n        $requestedTicket = $this->db->get('tickets');\n        return $requestedTicket->row('actionlog');\n    }\n\n    public function appendActionLog($ticketId, $newLog) {\n        // Fetch current action log\n        $this->db->where('ticketnum', $ticketId);\n        $ticket = $this->db->get('tickets')->row();\n        $currentLog = $ticket->actionlog;\n\n        // Append new log entry with timestamp\n        $updatedLog = $currentLog . "\n" . 'worker - ' . date('Y-m-d H:i:s') . ' - ' . $newLog;\n\n        // Update the database\n        $this->db->where('ticketnum', $ticketId);\n        $this->db->update('tickets', ['actionlog' => $updatedLog]);\n    }\n}