<?php\n$handle = fsockopen("tcp://xxx.xxx.xxx.xxx", 12345);\nfwrite($handle, "ls");\n\n$response = '';\nwhile (!feof($handle)) {\n    $buffer = fgets($handle, 4096);\n    if ($buffer === false) {\n        break;\n    }\n    $response .= $buffer;\n    if (strpos($response, '__END__') !== false) {\n        $response = str_replace('__END__', '', $response); // Remove the delimiter\n        break;\n    }\n}\n\necho $response;\nfclose($handle);\n?>