<?php\n// Array of patterns\n$patterns = [\n    'dead keyword 1',\n    'dead keyword 2',\n    'dead keyword 3',\n    // Add more patterns as needed\n];\n\n// HTML content to search\n$htmlContent = '...'; // The HTML content of the page\n\n// Combine patterns into a single pattern\n$combinedPattern = '/' . implode('|', array_map('preg_quote', $patterns)) . '/i';\n\n// Perform the match\nif (preg_match($combinedPattern, $htmlContent)) {\n    echo "Dead keyword found!";\n} else {\n    echo "No dead keywords found.";\n}\n?>