require_once 'path/to/htmlpurifier/library/HTMLPurifier.auto.php';\n\n$config = HTMLPurifier_Config::createDefault();\n\n// Allow only specific elements and attributes\n$config->set('HTML.AllowedElements', 'a, p, b, i, strong, em'); // specify allowed elements\n$config->set('HTML.AllowedAttributes', 'a.href, a.title, img.src, img.alt'); // specify allowed attributes\n\n// Purifier instance\n$purifier = new HTMLPurifier($config);\n\n// Example HTML input\n$dirty_html = '<p><a href="http://example.com" title="example" onclick="alert(\'XSS\')">Click me</a></p>';\n\n// Clean the HTML\n$clean_html = $purifier->purify($dirty_html);\n\necho $clean_html;