<?php\ndefine('FILE_PATH', 'last_value.txt');\n\nfunction getAlternatingValue() {\n    // Check if the file exists\n    if (!file_exists(FILE_PATH)) {\n        // If the file does not exist, create it and initialize with 0\n        file_put_contents(FILE_PATH, '0');\n        return 0;\n    }\n\n    // Read the last value from the file\n    $lastValue = file_get_contents(FILE_PATH);\n\n    // Determine the next value (alternating between 0 and 1)\n    $nextValue = ($lastValue == '0') ? '1' : '0';\n\n    // Update the file with the new value\n    file_put_contents(FILE_PATH, $nextValue);\n\n    // Return the new value as an integer\n    return (int)$nextValue;\n}\n\n// Example usage\n$value = getAlternatingValue();\necho $value;\n?>