<?php\nclass User extends CI_Controller {\n\n    public function __construct() {\n        parent::__construct();\n        // Load necessary models, libraries, etc.\n    }\n\n    public function index($username = null) {\n        if ($username === null) {\n            // Load default profile page\n            $this->load->view('default_profile');\n        } else {\n            // Access the database and get info for $username\n            $data['user_info'] = $this->user_model->get_user_info($username);\n\n            if ($data['user_info']) {\n                // Load profile page with user info\n                $this->load->view('user_profile', $data);\n            } else {\n                // Handle user not found, maybe show a 404 page\n                show_404();\n            }\n        }\n    }\n}\n?>