class PHPSO_Config extends ArrayObject\n{\n    public function __construct()\n    {\n        // Define the configuration array\n        $config = array(\n            'adminEmail' => 'user@domain.tld'\n        );\n        \n        // Pass the configuration array to the parent ArrayObject constructor\n        parent::__construct($config, ArrayObject::ARRAY_AS_PROPS);\n    }\n}\n\n// Usage in your main class\nclass MainClass\n{\n    private $config;\n\n    public function __construct()\n    {\n        require 'config.php';\n        $this->config = new PHPSO_Config();\n    }\n\n    public function getAdminEmail()\n    {\n        return $this->config->adminEmail;\n    }\n}\n\n// Example usage\n$main = new MainClass();\necho $main->getAdminEmail(); // Outputs: user@domain.tld