// Initialize the prepared statement\n$state = $mysqli->stmt_init();\n\n// Prepare filters and values\n$values = array();\n$filters = array();\nforeach ($item_list as $item) {\n    $filters[] = "(ItemNmbr = ? OR ItemDesc LIKE ? OR NoteText LIKE ?)";\n    $values[] = $item;\n    $values[] = '%' . $item . '%';\n    $values[] = '%' . $item . '%';\n}\n\n// Prepare the query\n$sql = sprintf(\n    "SELECT ItemNmbr, ItemDesc, NoteText, Iden, BaseUOM FROM ItemMaster WHERE %s LIMIT 21",\n    implode(" OR ", $filters)\n);\n\n// Prepare the statement\nif (!$state->prepare($sql)) {\n    die("Could not prepare statement: " . $mysqli->error);\n}\n\n// Type string for bind_param\n$types = str_repeat("s", count($values));\n\n// Bind parameters\n$state->bind_param($types, ...$values);\n\n// Execute the statement\nif (!$state->execute()) {\n    die("Could not execute statement: " . $state->error);\n}\n\n// Bind results\n$state->bind_result($ItemNmbr, $ItemDesc, $NoteText, $Iden, $BaseUOM);\n\n// Fetch and process the results\n$state->store_result();\nwhile ($state->fetch()) {\n    // Process each row\n    echo "ItemNmbr: $ItemNmbr, ItemDesc: $ItemDesc, NoteText: $NoteText, Iden: $Iden, BaseUOM: $BaseUOM\n";\n}\n\n// Close the statement\n$state->close();