<?php\n// Include SphinxClient\nrequire_once('sphinxapi.php');\n\n// Create a SphinxClient instance\n$cl = new SphinxClient();\n\n// Set server and port\n$cl->SetServer('localhost', 9312);\n\n// Perform the search\n$result = $cl->Query('your search query', 'your_index');\n\n// Check for errors\nif ($result === false) {\n    echo "Query failed: " . $cl->GetLastError();\n    exit;\n} elseif ($cl->GetLastWarning()) {\n    echo "WARNING: " . $cl->GetLastWarning();\n}\n\n// Get the matching document IDs\n$ids = array_keys($result['matches']);\n?>