<?php\n$mysqli = new mysqli("your_host", "your_username", "your_password", "your_database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$query = "\n    SELECT f.forum_id, f.forum_name, f.forum_description, MAX(c.comment_date) AS last_comment_date\n    FROM forums f\n    LEFT JOIN threads t ON f.forum_id = t.forum_id\n    LEFT JOIN comments c ON t.thread_id = c.comment_thread_id\n    GROUP BY f.forum_id\n    ORDER BY f.forum_order ASC";\n\n$stmt = $mysqli->prepare($query);\n$stmt->execute();\n$result = $stmt->get_result();\n\nwhile ($row = $result->fetch_assoc()) {\n    echo "<h3>Forum: " . htmlspecialchars($row['forum_name']) . "</h3>";\n    echo "<div>Desc: " . htmlspecialchars($row['forum_description']) . "</div>";\n    echo "<div>Last post: " . htmlspecialchars($row['last_comment_date']) . "</div>";\n}\n\n$stmt->close();\n$mysqli->close();\n?>