<?php\n// Define your key and initialization vector\ndefine('ENCRYPTION_KEY', 'your-secret-key');\ndefine('ENCRYPTION_IV', 'your-iv'); // Must be 16 bytes for AES-256-CBC\n\nfunction encrypt($data) {\n    $key = hash('sha256', ENCRYPTION_KEY, true);\n    $iv = substr(hash('sha256', ENCRYPTION_IV), 0, 16);\n    $encryptedData = openssl_encrypt($data, 'AES-256-CBC', $key, 0, $iv);\n    return base64_encode($encryptedData);\n}\n\nfunction decrypt($data) {\n    $key = hash('sha256', ENCRYPTION_KEY, true);\n    $iv = substr(hash('sha256', ENCRYPTION_IV), 0, 16);\n    $decryptedData = openssl_decrypt(base64_decode($data), 'AES-256-CBC', $key, 0, $iv);\n    return $decryptedData;\n}\n\n// Example usage\n$originalData = 'password123';\n$encryptedData = encrypt($originalData);\n$decryptedData = decrypt($encryptedData);\n\necho "Original Data: " . $originalData . "\n";\necho "Encrypted Data: " . $encryptedData . "\n";\necho "Decrypted Data: " . $decryptedData . "\n";\n?>