class PageController extends Zend_Controller_Action\n{\n    public function viewAction()\n    {\n        // Assuming $pageId is retrieved from the request\n        $pageId = $this->_getParam('id');\n        \n        // Fetch page and contents from the database\n        $pageModel = new Application_Model_Page();\n        $contentModel = new Application_Model_Content();\n        \n        $page = $pageModel->fetchPageById($pageId);\n        $contents = $contentModel->fetchContentsByPageId($pageId);\n        \n        // Pass page and contents to the view\n        $this->view->page = $page;\n        $this->view->contents = $contents;\n    }\n}