<?php\n   // index.php\n\n   $url = isset($_GET['url']) ? $_GET['url'] : '';\n\n   switch ($url) {\n       case '':\n           // Load homepage\n           include 'home.php';\n           break;\n       case 'register':\n           // Load registration page\n           include 'register.php';\n           break;\n       case 'login':\n           // Load login page\n           include 'login.php';\n           break;\n       default:\n           // Check for dynamic user profile\n           if (preg_match('/^user\/([a-zA-Z0-9_-]+)$/', $url, $matches)) {\n               $_GET['username'] = $matches[1];\n               include 'profile.php';\n           } else {\n               // 404 Not Found\n               include '404.php';\n           }\n           break;\n   }\n   ?>