class YourModel extends Zend_Db_Table_Abstract\n{\n    protected $_name = 'your_table_name';\n\n    public function insert(array $data)\n    {\n        // Preprocess the data to set empty values to NULL\n        foreach ($data as $key => $value) {\n            if ($value === '') {\n                $data[$key] = null;\n            }\n        }\n\n        // Use the parent insert method\n        return parent::insert($data);\n    }\n}\n\n// Usage example\n$model = new YourModel();\n$formData = [\n    'date_field' => '',\n    'integer_field' => '',\n    'other_field' => 'Some value'\n];\n$model->insert($formData);