public function login($username, $pass, $remember) {\n    // Database connection\n    $connect = new connect(); \n    $conn = $connect->login_connect();\n\n    // Use prepared statements to prevent SQL injection\n    $stmt = $conn->prepare("SELECT * FROM login WHERE username = ?");\n    $stmt->bind_param("s", $username);\n    $stmt->execute();\n    $result = $stmt->get_result();\n\n    if ($result->num_rows > 0) {\n        $row = $result->fetch_assoc();\n        \n        // Verify password\n        if (password_verify($pass, $row['password'])) {\n            // Assign id to session\n            $_SESSION['user_id'] = $row['user_id'];        \n            // Assign username as a session variable\n            $_SESSION['username'] = $username;\n            \n            // Start rememberMe\n            if ($remember) {\n                $cookie_name = 'db_auth';\n                $cookie_time = time() + (3600 * 24 * 30); // 30 days\n                $token = bin2hex(random_bytes(16)); // Generate a secure token\n                setcookie($cookie_name, $token, $cookie_time, "/", "", true, true); // Secure and HttpOnly flag\n                // Store the token in the database\n                $stmt = $conn->prepare("UPDATE login SET remember_token = ? WHERE user_id = ?");\n                $stmt->bind_param("si", $token, $row['user_id']);\n                $stmt->execute();\n            }\n\n            // If all goes well, redirect user to their homepage.\n            header('Location: http://localhost/v6/home/index.php');\n            exit();\n        } else {\n            throw new depException('Incorrect username and password combination. Please try again.');\n        }\n    } else {\n        throw new depException('Could not log you in.');\n    }\n}