<?php\nnamespace MyPackage;\n\nclass BaseClass {\n    private $privateVar = 'private';\n    protected $protectedVar = 'protected';\n    public $publicVar = 'public';\n\n    public function getPrivateVar() {\n        return $this->privateVar;\n    }\n\n    public function getProtectedVar() {\n        return $this->protectedVar;\n    }\n}\n\nclass SubClass extends BaseClass {\n    public function getBaseProtectedVar() {\n        return $this->protectedVar; // Accessible because it's protected\n    }\n\n    // private $privateVar from BaseClass is not accessible here\n}\n\n$base = new BaseClass();\necho $base->publicVar; // Accessible\n// echo $base->protectedVar; // Not accessible, will cause an error\n// echo $base->privateVar; // Not accessible, will cause an error\necho $base->getPrivateVar(); // Accessible through a public method\n\n$sub = new SubClass();\necho $sub->getBaseProtectedVar(); // Accessible through a method in the subclass\n?>