<?php\nsession_start();\n\n// For example purposes, let's assume the progress is stored in a session variable\nif (!isset($_SESSION['progress'])) {\n    $_SESSION['progress'] = 0;\n}\n\n// Simulate task progress\n$_SESSION['progress'] += 10;\n\n$progress = $_SESSION['progress'];\n\n// Reset progress when complete\nif ($progress >= 100) {\n    $_SESSION['progress'] = 0;\n}\n\nheader('Content-Type: application/json');\necho json_encode(array('progress' => $progress));\n?>